    //! @brief 리스트의 특정 위치에 새 노드를 추가하는 메서드
    //! @param index 인덱스
    //! @param value 값
    void InsertAt(int index, int value) noexcept {
        // (1) 인덱스 범위 검사
        if (index &lt; 0 || index &gt; m_Size) { return; }

        // (2) 인덱스가 0이라면
        if (index == 0) {
            Prepend(value);
            return;
        }

        // (3) 인덱스가 마지막이라면
        if (index == m_Size) {
            Append(value);
            return;
        }

        // (4) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (5) 반으로 나누어 진행 방향 결정, 삽입될 위치의 노드 취득
        Node* current = nullptr;
