        newNode-&gt;prev = current-&gt;prev;

        // (8) 현재 노드의 prev의 next를 새 노드로 설정
        current-&gt;prev-&gt;next = newNode;

        // (9) 현재 노드의 prev를 새 노드로 설정
        current-&gt;prev = newNode;

