//! @brief 노드
class Node {
private:
    int data;               //!&lt; 데이터
    Node* next;             //!&lt; 다음 노드를 가리키는 포인터

public:
    friend class SinglyCircularLinkedList;      // SinglyCircularLinkedList에서 접근하기 위해 필요

    //! @brief 생성자
    Node(int value) noexcept {
        this-&gt;data = value;
        this-&gt;next = nullptr;
    }
};

//! @brief 단일 원형 링크드 리스트
class SinglyCircularLinkedList {
private:
    Node* m_Head;           //!&lt; 헤드 노드
    int m_Size;             //!&lt; 리스트의 크기

public:
    //! @brief 생성자
    SinglyCircularLinkedList() noexcept {
        m_Head = nullptr;
        m_Size = 0;
    }

    //! @brief 소멸자
    ~SinglyCircularLinkedList() noexcept {
        Clear();
    }

    //! @brief 리스트 끝에 새 노드를 추가하는 메서드
    //! @param value 값
    void Append(int value) noexcept {
        // (1) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (2) 리스트가 비어 있다면
        if (m_Head == nullptr) {
            m_Head = newNode;
            m_Head-&gt;next = m_Head;              // 자기 자신을 가리킨다
            m_Size += 1;
            return;
        }

        // (3) 리스트에 노드가 있다면
        auto* current = m_Head;
        while (current-&gt;next != m_Head) {       // 마지막 노드로 이동
            current = current-&gt;next;
        }
        current-&gt;next = newNode;                // 새 노드 가리키기

        // (4) 원형 연결
        newNode-&gt;next = m_Head; 

        // (5) 크기 증가
        m_Size += 1;
    }

    //! @brief 리스트 처음에 새 노드를 추가하는 함수
    //! @param value 값
    void Prepend(int value) noexcept {
        // (1) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (2) 리스트가 비어 있다면
        if (m_Head == nullptr) {
            m_Head = newNode;
            m_Head-&gt;next = m_Head;          // 자기 자신을 가리킨다
            m_Size += 1;
            return;
        }

        // (3) 리스트에 노드가 있다면
        auto* current = m_Head;
        while (current-&gt;next != m_Head) {   // 마지막 노드로 이동
            current = current-&gt;next;
        }

        // (4) 새 노드의 next가 기존 헤드를 가리킨다
        newNode-&gt;next = m_Head;

        // (5) 마지막 노드가 새 노드를 가리킨다
        current-&gt;next = newNode;

        // (6) 헤드 갱신
        m_Head = newNode;

        // (7) 크기 증가
        m_Size += 1;
    }

    //! @brief 리스트의 특정 위치에 새 노드를 삽입하는 메서드
    //! @param index 인덱스
    //! @param value 값
    void InsertAt(int index, int value) noexcept {
        // (1) 인덱스 범위 검사
        if (index &lt; 0 || index &gt; m_Size) { return; }

        // (2) 인덱스가 0이라면
        if (index == 0) {
            Prepend(value);
            return;
        }

        // (3) 인덱스가 마지막이라면
        if (index == m_Size) {
