        auto* current = m_Head;
        for (auto i = 0; i &lt; index - 1; ++i) {
            current = current-&gt;next;
        }

        // (6) 새 노드가 다음 노드 가리키기
        newNode-&gt;next = current-&gt;next;

        // (7) 이전 노드가 새 노드 가리키기
        current-&gt;next = newNode;

        // (8) 크기 증가
        m_Size += 1;
    }

