    void DeleteBegin() noexcept {
        // (1) 리스트가 비어 있는 지 확인
        if (m_Head == nullptr) { return; }

        // (2) 제거할 노드 임시 보관
        auto* temp = m_Head;

        // (3) 노드가 하나라면
        if (m_Size == 1) {
            delete temp;
            m_Head = nullptr;
            m_Size = 0;
            return;
        }

        // (4) 테일 노드 찾기
        auto* tail = m_Head;
        while (tail-&gt;next != m_Head) {
            tail = tail-&gt;next;
        }

        // (5) 헤드 노드를 헤드 노드의 다음 노드를 가리킨다
        m_Head = m_Head-&gt;next;

        // (6) 테일 노드의 다음 노드를 헤드 노드를 가리킨다
        tail-&gt;next = m_Head;

        // (7) 제거
        delete temp;

        // (8) 크기 감소
        m_Size -= 1;
    }

    //! @brief 리스트의 마지막 노드를 제거하는 메서드
    void DeleteEnd() noexcept {
        // (1) 리스트가 비어 있다면
        if (m_Head == nullptr) { return; }

        // (2) 리스트에 노드가 하나 뿐인 지 확인
        if (m_Head-&gt;next == nullptr) {
            delete m_Head;
            m_Head = nullptr;
            m_Size = 0;
            return;
        }

        // (3) 마지막 노드의 이전 노드까지 이동
        auto* prev = m_Head;
        while (prev-&gt;next-&gt;next != m_Head) {
            prev = prev-&gt;next;
        }

        // (4) 제거 대상
        auto* target = prev-&gt;next;

        // (5) 제거 대상의 다음 노드는 헤드 노드를 가리킨다
        prev-&gt;next = m_Head;

        // (6) 제거
        delete target;

        // (7) 크기 감소
        m_Size -= 1;
    }

    //! @brief 리스트의 특정 위치에 있는 노드를 제거하는 메서드
    //! @param index 인덱스
    void DeleteAt(int index) noexcept {
        // (1) 인덱스 범위 검사
        if (index &lt; 0 || index &gt;= m_Size) { return; }

        // (2) 인덱스가 0이라면
        if (index == 0) {
            DeleteBegin();
            return;
        }

        // (3) 인덱스가 마지막이라면
        if (index == m_Size - 1) {
            DeleteEnd();
            return;
        }

        // (4) 제거 대상의 직전 노드 찾기
        auto* prev = m_Head;
        for (auto i = 0; i &lt; index - 1; ++i) {
            prev = prev-&gt;next;
        }

        // (6) 제거 대상
        auto* target = prev-&gt;next;

        // (7) 직전 노드의 다음 노드를 제거 대상의 다음 노드 가리키기
        prev-&gt;next = target-&gt;next;

        // (8) 제거
        delete target;

        // (9) 크기 감소
        m_Size -= 1;
    }

    //! @brief 모든 노드를 제거하는 메서드
    void Clear() noexcept {
        // (1) 리스트가 비어 있다면
        if (m_Head == nullptr) { return; }

        // (2) 순회 및 제거
        auto* current = m_Head;
        for (auto i = 0; i &lt; m_Size; ++i) {
            auto* target = current;
            current = current-&gt;next;
            delete target;
        }
        m_Head = nullptr;
        m_Size = 0;
    }

    void Print() noexcept {
