private:
    int data;               //!&lt; 데이터
    Node* prev;             //!&lt; 이전 노드를 가리키는 포인터
    Node* next;             //!&lt; 다음 노드를 가리키는 포인터

public:
    friend class DoublyCircularLinkedList;  // DoublyCircularLinkedList 접근하기 위해 필요

    //! @brief 생성자
    //! @param value 값
    Node(int value) noexcept {
        this-&gt;data = value;
        this-&gt;prev = this-&gt;next = nullptr;
    }
};

//! @brief 이중 원형 링크드 리스트
class DoublyCircularLinkedList {
private:
    Node* m_Head;           //!&lt; 헤드 노드
    int m_Size;             //!&lt; 크기 (리스트의 요소 수)

public:
    //! @brief 생성자
    DoublyCircularLinkedList() noexcept {
        m_Head = nullptr;
        m_Size = 0;
    }

    //! @brief 소멸자
    ~DoublyCircularLinkedList() noexcept {
        Clear();
    }

    //! @brief 리스트의 끝에 새 노드를 추가하는 메서드
    //! @param value 값
    void Append(int value) noexcept {
        // (1) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (2) 리스트가 비어 있다면
        if (m_Head == nullptr) {
            m_Head = newNode;

            // 자기 자신을 가리킨다
            newNode-&gt;prev = newNode;
            newNode-&gt;next = newNode;

            m_Size += 1;
            return;
        }

        // (3) 리스트에 노드가 있다면
        Node* last = m_Head-&gt;prev;      // 마지막 노드
        last-&gt;next = newNode;           // 새 노드를 가리킨다
        newNode-&gt;prev = last;           // 새 노드의 이전 노드는 마지막 노드를 가리킨다
        newNode-&gt;next = m_Head;         // 새 노드의 다음 노드는 헤드 노드를 가리킨다
        m_Head-&gt;prev = newNode;         // 헤드 노드의 이전 노드는 새 노드를 가리킨다

        // (4) 크기 증가
        m_Size += 1;
    }

    //! @brief 리스트의 처음에 새 노드를 추가하는 메서드
    //! @param value 값
    void Prepend(int value) noexcept {
        // (1) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (2) 리스트가 비어 있다면
        if (m_Head == nullptr) {
            m_Head = newNode;

            // 자기 자신을 가리킨다
            newNode-&gt;prev = newNode;
            newNode-&gt;next = newNode;

            m_Size +=- 1;
            return;
        }

        // (3) 리스트에 노드가 있다면
        auto* last = m_Head-&gt;prev;      // 마지막 노드
        newNode-&gt;next = m_Head;         // 새 노드의 다음 노드가 헤드 노드를 가리킨다
        newNode-&gt;prev = last;           // 새 노드의 이전 노드가 마지막 노드를 가리킨다
        m_Head-&gt;prev = newNode;         // 헤드 노드의 이전 노드가 새 노드를 가리킨다
        last-&gt;next = newNode;           // 마지막 노드의 다음 노드가 새 노드를 가리킨다
        m_Head = newNode;               // 헤드 갱신

        // (4) 크기 증가
        m_Size += 1;
    }

    //! @brief 리스트의 특정 위치에 새 노드를 추가하는 메서드
    //! @param index 인덱스
    //! @param value 값
    void InsertAt(int index, int value) noexcept {
        // (1) 인덱스 범위 검사
        if (index &lt; 0 || index &gt; m_Size) { return; }

        // (2) 인덱스가 0이라면
        if (index == 0) {
            Prepend(value);
            return;
        }

        // (3) 인덱스가 마지막이라면
        if (index == m_Size) { 
            Append(value);
            return;
        }

        // (4) 새 노드 생성
        auto* newNode = new Node(value);
        if (!newNode) { return; }

        // (5) 반으로 나누어 진행 방향 결정, 삽입 위치의 노드로 이동
        Node* current = nullptr;
        if ((m_Size / 2) &lt; index) {
            current = m_Head;                               // 헤드에서 시작
            for (auto i = 0; i &lt; index; ++i) {
                current = current-&gt;next;
            }
        } else {
            current = m_Head-&gt;prev;                         // 테일에서 시작
            for (auto i = m_Size - 1; i &gt; index; --i) {
                current = current-&gt;next;
            }
        }

        // (6) 노드 추가
        auto* prev = current-&gt;prev;
        prev-&gt;next = newNode;
        newNode-&gt;prev = prev;
        newNode-&gt;next = current;
        current-&gt;prev = newNode;

        // (7) 크기 증가
        m_Size += 1;
    }

    //! @brief 리스트의 첫 노드를 제거하는 메서드
    void DeleteBegin() noexcept {
        // (1) 리스트가 비어 있는 지 확인
        if (m_Head == nullptr) { return; }

        // (2) 제거 대상 노드 임시 보관
        auto* target = m_Head;

        // (3) 리스트의 노드가 하나만 있다면
        if (m_Size == 1) {
            delete target;
            m_Head = nullptr;
            m_Size -= 1;
            return;
        }

        // (4) 리스트의 노드가 여러 개 있다면
        auto* tail = m_Head-&gt;prev;          // 테일 노드
        m_Head = m_Head-&gt;next;              // 헤드 노드를 헤드 노드의 다음 노드를 가리킨다
        m_Head-&gt;prev = tail;                // 헤드 노드의 이전 노드가 테일 노드를 가리킨다
        tail-&gt;next = m_Head;                // 테일 노드의 다음 노드가 헤드 노드를 가리킨다

        // (5) 제거
        delete target;

        // (6) 크기 감소
        m_Size -= 1;
    }

    //! @brief 리스트의 마지막 노드를 제거하는 메서드
    void DeleteEnd() noexcept {
        // (1) 리스트가 비어 있다면
        if (m_Head == nullptr) { return; }

        // (3) 리스트에 노드가 하나 뿐이라면
        if (m_Size == 1) {
            delete m_Head;
            m_Head = nullptr;
            m_Size = 0;
            return;
        }

        // (3) 제거 대상 노드
        auto* target = m_Head-&gt;prev;        // 제거 대상
        auto* newTail = target-&gt;prev;       // 새 테일이 되는 대상

        // (5) 새 테일의 다음 노드를 헤드 노드로 가리킨다
        newTail-&gt;next = m_Head;

        // (6) 헤드 노드의 이전 노드를 새 테일로 가리킨다
        m_Head-&gt;prev = newTail;

        // (7) 제거
        delete target;

        // (8) 크기 감소
        m_Size -= 1;
    }

    //! @brief 리스트의 특정 위치에 있는 노드를 제거하는 메서드
    //! @param index 인덱스
    void DeleteAt(int index) noexcept {
        // (1) 인덱스 범위 검사
        if (index &lt; 0 || index &gt;= m_Size) { return; }

        // (3) 인덱스가 0이라면
        if (index == 0) {
            DeleteBegin();
            return;
        }

        // (4) 인덱스가 끝이라면
        if (index == m_Size - 1) {
            DeleteEnd();
            return;
        }

        // (5) 반으로 나누어 진행 방향 결정, 제거할 노드 취득
        Node* current = nullptr;
        if ((m_Size / 2) &lt; index) {         // 헤드에서 시작
            current = m_Head;
            for (auto i = 0; i &lt; index; ++i) {
                current = current-&gt;next;
            }
        } else {                                // 테일에서 시작
            current = m_Head-&gt;prev;
            for (auto i = m_Size - 1; i &gt; index; --i) {
                current = current-&gt;prev;
            }
        }

        // (6) 연결 끊기
        current-&gt;prev-&gt;next = current-&gt;next;
        current-&gt;next-&gt;prev = current-&gt;prev;

        // (7) 제거
        delete current;

        // (8) 크기 감소
        m_Size -= 1;
    }

    //! @brief 모든 노드를 제거하는 메서드
    void Clear() noexcept {
        // (1) 리스트가 비어 있다면
        if (m_Head == nullptr) { return; }

        // (2) 순회 및 제거
        auto* current = m_Head;
        for (auto i = 0; i &lt; m_Size; ++i) {
            auto* target = current;
            delete target;
            current = current-&gt;next;
        }
    }

    //! @brief 순회 및 출력
    void Print() noexcept {
        // (1) 리스트가 비어 있다면
        if (m_Head == nullptr) { return; }

        // (2) 순회 및 출력
        auto* current = m_Head;
        for (auto i = 0; i &lt; m_Size; ++i) {
            std::cout &lt;&lt; current-&gt;data &lt;&lt; " ";
            current = current-&gt;next;
        }
        std::cout &lt;&lt; "\n";
    }
};

int main(int argc, char* argv[]) {
    // 이중 원형 링크드 리스트
    DoublyCircularLinkedList dcll;

    // 맨 뒤 데이터 추가
    dcll.Append(10);
    dcll.Append(30);
    dcll.Append(20);

    // 중간 데이터 삭제
    dcll.DeleteAt(1);

    // 출력
    dcll.Print();

    return 0;
}
</code></pre>

hashtable.cpp<pre><code>#include &lt;functional&gt;       // (1)
#include &lt;iostream&gt;
#include &lt;list&gt;             // (2)
#include &lt;string_view&gt;
#include &lt;vector&gt;

//! @brief 테이블 크기 (소수 설정)
constexpr auto TABLE_SIZE = 101;

//! @brief 해시 테이블
class HashTable {
private:
    //! @brief 엔트리
    //! @note 키-값 쌍을 저장하는 구조체
    struct Entry {
        std::string key;
        int value;
    };

    std::vector&lt;std::list&lt;Entry&gt;&gt; m_Buckets;        //!&lt; 버킷 배열
    size_t m_Size;                                  //!&lt; 저장된 엔트리의 수

    //! @brief 해시 메서드
    //! @param key 키
    //! @param size 테이블 크기
    //! @return 해시 값
    size_t hash(std::string_view key) noexcept const {
        return std::hash&lt;std::string_view&gt;{}(key) % TABLE_SIZE;
    }

public:
    //! @brief 생성자
    HashTable() noexcept {
        m_Buckets.resize(TABLE_SIZE);
        m_Size = 0;
    }

    //! @brief 소멸자
    ~HashTable() noexcept {
        for (auto&amp; bucket : m_Buckets) {
            bucket.clear();
        }
    }
};
</code></pre> <ol> <li><code>std::hash</code> 사용을 위해 포함</li> <li>체이닝을 위해 연결 리스트 사용</li> </ol> <p><code>Entry</code>는 하나의 데이터를 나타내는 구조체로, 키와 값을 가집니다. 엔트리는 해시 테이블에서 관습적으로 사용되는 용어로, 테이블 안의 데이터를 의미합니다.</p> <p><code>m_Buckets</code>는 버킷 배열로, 각 요소는 <code>std::list</code> 컨테이너를 사용하고 있습니다. 링크드 리스트로, 체이닝 구조를 기반으로 합니다.</p> <p><code>m_Size</code>는 실제 저장된 키-값(엔트리)의 개수입니다. 리사이징을 고려해서 추가한 멤버 변수인데, 복잡해질까봐 리사이징은 생략했습니다.</p> <p><code>hash()</code> 메서드는 C++의 표준 해시 함수를 사용했습니다.</p>

